package org.bk.algo.general.t30day;

import org.junit.jupiter.api.Test;

import static org.assertj.core.api.Assertions.assertThat;

class RemoveKDigits {
    public String removeKdigits(String num, int k) {
        final String res = allDigits("", num, num.length() - k);
        return res != null ? removeLeadingZero(res) : "0";
    }

    private String removeLeadingZero(String s) {
        int idx = 0;
        int len = s.length();
        while (idx < len && s.charAt(idx) == '0') {
            idx++;
        }
        String res = s.substring(idx);
        if (res.length() == 0) {
            return "0";
        }
        return res;
    }

    public String allDigits(String prefix, String remaining, int k) {
        if (k == 0) {
            return prefix.length() > 0 ? prefix : null;
        }
        if (remaining.length() == 0 && k > 0) {
            return null;
        }
        String set1 = allDigits(prefix + remaining.charAt(0),
                remaining.length() > 0 ? remaining.substring(1) : "",
                k - 1);
        String set2 = allDigits(prefix,
                remaining.length() > 0 ? remaining.substring(1) : "",
                k);

        if (set1 != null && set2 != null) {
            return smaller(set1, set2) ? set1 : set2;
        }

        if (set1 == null) {
            return set2;
        }

        if (set2 == null) {
            return set1;
        }
        return null;
    }

    private boolean smaller(String s1, String s2) {
        if (s1.length() < s2.length()) {
            return true;
        } else if (s1.length() > s2.length()) {
            return false;
        }

        if (s1.length() == 0) return true;

        for (int i = 0; i < s1.length(); i++) {
            if (s1.charAt(i) < s2.charAt(i)) {
                return true;
            } else if (s1.charAt(i) > s2.charAt(i)) {
                return false;
            }
        }
        return true;
    }

    @Test
    void testAllDigits() {
        assertThat(removeKdigits(
                "",
                1000)).isEqualTo("9909");
    }

    @Test
    void testSmaller() {
        assertThat(smaller("12", "13")).isTrue();
        assertThat(smaller("121", "13")).isFalse();
    }

    @Test
    void testRemoveLeadingZero() {
        assertThat(removeLeadingZero("012")).isEqualTo("12");
    }
}